package com.example.newsfeedapp.adapters;import android.content.Context;import android.os.Build;import androidx.annotation.NonNull;import androidx.annotation.RequiresApi;import androidx.recyclerview.widget.RecyclerView;import android.text.Html;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.example.newsfeedapp.R;import com.example.newsfeedapp.model.ListItemNews;import com.squareup.picasso.Picasso;import java.util.ArrayList;import java.util.List;public class ListAdapterNews extends RecyclerView.Adapter<ListAdapterNews.ListViewHolder> {    public interface onItemClick {        void onClick(View view, int position);    }    private Context context;    private List<ListItemNews> newsFeedList;    private int id;    private onItemClick onItemClick;    public ListAdapterNews(Context context, int id, List<ListItemNews> NewsFeedList, onItemClick onItemClick) {        this.context = context;        this.newsFeedList = NewsFeedList;        this.onItemClick = onItemClick;        this.id = id;    }    @RequiresApi(api = Build.VERSION_CODES.LOLLIPOP)    @NonNull    @Override    public ListViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        if(id == 1) {            return new ListViewHolder(LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.list_item_feed, viewGroup, false));        } else {            return new ListViewHolder(LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.list_item_feed_pinterest, viewGroup, false));        }    }    @Override    public void onBindViewHolder(@NonNull ListViewHolder listViewHolder, int i) {        listViewHolder.bindView(newsFeedList.get(i));    }    public void updateList(ArrayList<ListItemNews> newsArrayList) {        newsFeedList.clear();        newsFeedList.addAll(newsArrayList);        notifyDataSetChanged();    }    @Override    public int getItemCount() {        return newsFeedList.size();    }    class ListViewHolder extends RecyclerView.ViewHolder {        TextView newsTitle, newsCategory ;        ImageView newsImage;        @RequiresApi(api = Build.VERSION_CODES.LOLLIPOP)        public ListViewHolder (View itemView) {            super(itemView);            newsTitle = itemView.findViewById(R.id.articleTitle);            newsCategory = itemView.findViewById(R.id.articleCategory);            newsImage = itemView.findViewById(R.id.articleIntroImage);            itemView.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    onItemClick.onClick(v, getAdapterPosition());                }            });        }        public void bindView (ListItemNews itemNews) {            newsTitle.setText((Html.fromHtml(itemNews.getTitle()).toString()));            newsCategory.setText((Html.fromHtml(itemNews.getCategory()).toString()));            Picasso.with(context)                    .load(itemNews.getImageUrl())                    .into(newsImage);        }    }}